const minutesSummary = {{summarizeMinutes.generatedText}};
const formattedSummary =`
<h1>Meeting Summary</h1>
<p>${minutesSummary.summary}</p>
<h2>Key Points</h2>
<ul>
${minutesSummary.keyPoints.map(point =>`<li>${point}</li>`).join('')}
</ul>
<h2>Action Items</h2>
<ul>
${minutesSummary.actionItems.map(item =>`<li>${item}</li>`).join('')}
</ul>
<h2>Attendees</h2>
<p>${minutesSummary.attendees.join(', ')}</p>
`;

return { formattedSummary, attendees: minutesSummary.attendees, attendeesToCC: minutesSummary.attendeesToCC };

const logEntry = {
 timestamp: new Date().toISOString(),
 meetingSubject: {{receiveMeetingMinutes.subject}},
 attendees: {{formatSummary.result.attendees}},
 summary: {{summarizeMinutes.generatedText}}
};

// In a real scenario, you would send this data to a database or logging service
console.log('Meeting Summary Log Entry:', JSON.stringify(logEntry, null, 2));

return { logged: true };
