const requestDetails = JSON.parse({{analyzeRequest.generatedText}});
const generatedContent = {{generateContent.generatedText}};

const response = `Dear Content Requester,

Thank you for your content request. We've generated the following content based on your specifications:

Content Type: ${requestDetails.contentType}
Topic: ${requestDetails.topic}
Target Audience: ${requestDetails.targetAudience}
Tone: ${requestDetails.tone}

Generated Content:

${generatedContent}

We hope this meets your requirements. If you need any revisions or have any questions, please don't hesitate to reply to this email.

Best regards,
Your Content Generation Team`;

return { formattedResponse: response };
